launch/demo.launch<pre><code>&lt;launch&gt;
  &lt;!-- 参数 --&gt;
  &lt;arg name="use_sim" default="true"/&gt;
  &lt;param name="robot_name" value="my_robot"/&gt;

  &lt;!-- 加载 YAML 参数 --&gt;
  &lt;rosparam file="$(find my_pkg)/config/params.yaml" command="load"/&gt;

  &lt;!-- 启动节点 --&gt;
  &lt;node pkg="my_pkg" type="talker.py" name="talker" output="screen"/&gt;
  &lt;node pkg="my_pkg" type="listener.py" name="listener" output="screen"/&gt;

  &lt;!-- 条件启动 --&gt;
  &lt;group if="$(arg use_sim)"&gt;
    &lt;include file="$(find gazebo_ros)/launch/empty_world.launch"/&gt;
  &lt;/group&gt;
&lt;/launch&gt;
</code></pre>

launch/demo.launch.py<pre><code>from launch import LaunchDescription
from launch.actions import DeclareLaunchArgument
from launch.substitutions import LaunchConfiguration
from launch_ros.actions import Node

def generate_launch_description():
    return LaunchDescription([
        # 声明参数
        DeclareLaunchArgument('use_sim_time', default_value='false'),

        # 启动节点
        Node(
            package='my_pkg',
            executable='talker',
            name='talker',
            output='screen',
            parameters=[{
                'use_sim_time': LaunchConfiguration('use_sim_time'),
            }],
            remappings=[
                ('/cmd_vel', '/robot/cmd_vel'),
            ],
        ),

        Node(
            package='my_pkg',
            executable='listener',
            name='listener',
            output='screen',
        ),
    ])
</code></pre>

